import React, { useState, useEffect } from "react";

const _useList = () => {
	const [data, setData] = React.useState(store.getState()[namespace].list);

	React.useEffect(() => {
		const unsubscribe = store.subscribe(() => {
			const newData = store.getState()[namespace].list;
			if (newData === data) return;
			setData(newData);
		});
		return () => unsubscribe();
	}, [namespace]);

	return data;
};

const Test = () => {
	const [count, setCount] = useState(0);

	const tick = () => {
		setCount(count + 1);
	};

	console.dir(tick);

	useEffect(() => {
		setInterval(tick, 1000);
	}, []);

	return <button>{count}</button>;
};

export default Test;
