import React, { useEffect } from "react";

let states = [];

let calls = -1;

const useState = initialValue => {
	let callId = ++calls;
	let value = initialValue;

	const setValue = newValue => {
		states[callId][0] = newValue;
	};

	let tuple = [value, setValue];

	states[callId] = tuple;

	return tuple;
};

const Test = () => {
	let [count, setCount] = useState(0);
	console.log(count, setCount, states);
	return (
		<div>
			<h1>{count}</h1>
			<button
				onClick={() => {
					setCount(count + 1);
				}}
			>
				Increment
			</button>
		</div>
	);
};

export default Test;
